package com.vstl.javaTraining;

public class ControlStatementsIfelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int salary = 30000;
		if (salary==20000)
		{
			System.out.println("Executing first statement\n");
			}
		else {			
				System.out.println("Executing Else statement\n");
			}
		}
	}


